import { createFeatureSelector, createReducer, createSelector, on } from "@ngrx/store";
import * as ReservationAction from "./reservation.action";
;
const initialState = {
  weeklyReservation: {
    weekNr: 0,
    numberOfReservations: 0
  },
  deadline: new Date()
};
export const reservationFeatureKey = 'reservations';
const getReservationFeatureSector = createFeatureSelector(reservationFeatureKey);
export const getWeeklyReservation = createSelector(getReservationFeatureSector, state => state.weeklyReservation);
export const getDeadline = createSelector(getReservationFeatureSector, state => state.deadline);
export const reservationReducer = createReducer(initialState, on(ReservationAction.setWeeklyReservation, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    weeklyReservation: action.weeklyReservation
  });
}), on(ReservationAction.setDeadline, (state, action) => {
  return Object.assign(Object.assign({}, state), {
    deadline: action.deadline
  });
}));
